}
img {
  /*width: 100%;*/
 max-width: 100%;
  border: 0;
  margin: 0px;
}
p + p {
  padding-top: 0px;
}

/*h1,h2,h3,h6,b{font-weight:normal;}*/

/*font*/

body {
  font-family: 'PingFang SC', 'Hiragino Sans GB', 'Microsoft Yahei', 'WenQuanYi Micro Hei', sans-serif;
  max-width: 100%;
  padding: 0px 0px 20px 0px;
  color: #808080;
  background: #fff;
}

/*文本两端对齐*/

p {
text-align:left;
}

/*content*/

.container {
  max-width: 768px;
  display: inline-block;
  background-color: #fff;
  margin: 0px auto;
  box-shadow: none;
  -webkit-box-shadow: none;
  word-break:break-word;
}
header {width:100%;
  display: block;
  margin-bottom: 10px;
  cursor:pointer;
}
header img{width:100%;display:block;overflow:hidden;}
blockquote {
	padding: 10px 15px;
	max-width: 540px;
	text-align: left;
	margin: 10px 30px;
	font-size: 14px;
	color: #B3B3B3;
	line-height: 25px;
	background: #F7F7F7;
	display: inline-block;
}
h1 {
	color: #000000;
  font-size: 24px;
  width: 100%;
  text-align: center;
  padding: 20px;
	font-weight: normal;
	margin: 0px;
}
h2{
  color: #000000;
  letter-spacing: 1px;
  line-height: 30px;
  padding: 30px 30px 0px;
  margin: 0px auto;
  font-weight: bold;
  width: 100%;
  max-width: 600px;
  text-align: left;
	font-size: 18px;
}
h3 {
	color: #000000;
	font-size: 15px;
  letter-spacing: 1px;
  line-height: 30px;
  padding: 10px 30px 0px;
  margin: 0px auto;
  /*font-weight: normal;*/
  width: 100%;
  max-width: 600px;
  text-align: left;
}

p {
  margin: 0px auto;
  padding: 10px 30px;
  max-width: 600px;
  font-size: 15px;
  letter-spacing: 0.6px;
  line-height: 30px;
  width: 100%;
  text-align: left;
}
h3 + h3 {
  padding-top: 5px;
}

figure{max-width: 600px;margin:0px auto;}

figure img {
  display: block;
  /*width: 100%;*/
  /*max-width: 600px;*/
  border: 0px;
  margin:0px auto;
  padding:0px 0px 30px 0px;
/*  min-height: 100px;*/
}
figcaption {
  font-size: 12px;
  color: #BFBFBF;
  letter-spacing: 0.48px;
  text-align: center;
  margin-bottom: 10px;
  padding:0px 30px;
}
ul, ol {
  width: 100%;
  max-width: 600px;
  padding: 10px 30px 10px 50px;
  margin: 0px auto;
  line-height: 30px;
  text-align: left;
  letter-spacing: 0.6px;
  font-size: 15px;
}
li {
  width: 100%;
}
hr {
  border: none;
  height: 1px;
  max-width: 600px;
  margin: 20px auto;
  background: #EFEFEF;
  border-left: #fff 30px solid;
  border-right: #fff 30px solid;
}
address {
  text-align: center;
  margin: 0px auto;
}
.user-info-img, .user-info-name {
  display: none;
}
time {
  display: inline-block;
  font-size: 13px;
  color: #bfbfbf;
  letter-spacing: 1px;
  text-align: center;
  padding: 40px 30px;
  max-width: 600px;
  width: 100%;
	font-style: normal;
	font-weight: normal;
}

/*pc|ipad*/

.head-box {
  width: 100%;
  height: 500px;
  background-repeat: no-repeat;
  background-position: center center;
  background-size: cover;
}
@media only screen and (min-width:770px) {
  h1 {
    font-size: 30px;
		padding: 30px;
  }
  .footbar {
    margin-bottom: 20px;
  }
  body {
    background-color: #ECF0F1 !important;
    padding: 0px;
  }
  .container {
    margin-top: 50px;
    margin-bottom: 100px;
    box-shadow: 0px 30px 60px 0px #D3DDE2;
    -webkit-box-shadow: 0px 30px 60px 0px #D3DDE2;
  }
}
@media only screen and (max-width:700px) {
  figure img {
    cursor:pointer;
  }
}

/* zoom.css */

img[data-action="zoom"] {
  cursor: pointer;
  cursor: -webkit-zoom-in;
  cursor: -moz-zoom-in;
}
.zoom-img, .zoom-img-wrap {
  position: relative;
  z-index: 666;
  -webkit-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
img.zoom-img {
  cursor: pointer;
  cursor: -webkit-zoom-out;
  cursor: -moz-zoom-out;
}
.zoom-overlay {
  z-index: 420;
  background: #fff;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  filter: "alpha(opacity=0)";
  opacity: 0;
  -webkit-transition: opacity 300ms;
  -o-transition: opacity 300ms;
  transition: opacity 300ms;
}
.zoom-overlay-open .zoom-overlay {
  filter: "alpha(opacity=100)";
  opacity: 1;
}
.zoom-overlay-open, .zoom-overlay-transitioning {
  cursor: default;
}

