p code{background-color: #fafafa !important;border: 1px solid hsl(0, 0%, 90%);border-radius: 5px !important;white-space: normal !important;padding: 0px 5px;}
.tl{text-align:left;}
.tr{text-align:right;}
.tc{text-align:center;}
/*pc|ipad*/
@media only screen and (min-width:751px){
	p,h1,h2,h3,hr,ul,ol,figure,.footbar,blockquote,address,figcaption,pre{margin-left:auto;margin-right:auto;max-width:750px;}
}
